import React, { useState } from 'react'
import AddIcon from '@mui/icons-material/Add';
import Button from '@mui/material/Button';
import DeleteIcon from '@mui/icons-material/Delete';
import { Grid, Paper, Typography } from '@mui/material';
import { Box } from '@mui/system';
import Item from './Item';

export default function ButtonItem() {
    const [item, setItem] = useState(0);
    const handleClick = () => {
        setItem(count => count + 1);
        <Item/>
    };
    const handleReset = () => {
        setItem(0)
    }
    return (
        <>
            <Box
                sx={{
                    p: 2,
                    bgcolor: 'background.default',
                    display: 'grid',
                    justifyContent: 'center'
                }}
            >
                <Paper elevation={3} sx={{ p: 2, width: 450 }} >
                    <Typography variant="h3" className="counter__output">{item}</Typography>
                    <Grid >
                        <Button variant="outlined" startIcon={<AddIcon />} onClick={handleClick}>
                            Add Item
                        </Button>
                        <Button variant="outlined" startIcon={<DeleteIcon />} onClick={handleReset}>
                            Reset
                        </Button>
                    </Grid>
                </Paper>
                </Box>
                {handleClick ? <Item/> : ''}
        </>
    )
}
